INSERT INTO `app_plugin_library` VALUES
('744C7174AA8C8A2410BA94C933C62067','recurso.html','2025-10-19 17:58:24','2025-10-19 18:20:27',1,'2BAA56D57128A664AD18DF268E31E49E','<!DOCTYPE html>\r\n<html lang=\"es\">\r\n<head>\r\n  <meta charset=\"UTF-8\">\r\n  <title>Procesar CSV y convertir a JSON</title>\r\n  <style>\r\n    body { font-family: sans-serif; padding: 20px; }\r\n    input { margin-bottom: 10px; }\r\n    table { border-collapse: collapse; margin-top: 1em; width: 100%; }\r\n    th, td { border: 1px solid #ccc; padding: 6px 10px; vertical-align: top; }\r\n    th { background: #eee; }\r\n    pre { background: #f7f7f7; border: 1px solid #ccc; padding: 10px; margin-top: 20px; white-space: pre-wrap; }\r\n  </style>\r\n</head>\r\n<body>\r\n  <h1>Procesar CSV avanzado (solo con JavaScript)</h1>\r\n  <input type=\"file\" id=\"csvFile\" accept=\".csv\">\r\n  <table id=\"table\"></table>\r\n  <h2>Resultado en JSON</h2>\r\n  <pre id=\"jsonOutput\"></pre>\r\n\r\n  <script>\r\n    const input = document.getElementById(\'csvFile\');\r\n    const table = document.getElementById(\'table\');\r\n    const jsonOutput = document.getElementById(\'jsonOutput\');\r\n\r\n    input.addEventListener(\'change\', () => {\r\n      const file = input.files[0];\r\n      if (!file) return;\r\n\r\n      const reader = new FileReader();\r\n      reader.onload = e => {\r\n        const text = e.target.result;\r\n        const data = parseCSVAdvanced(text);\r\n        renderTable(data);\r\n        renderJSON(data);\r\n      };\r\n      reader.readAsText(file);\r\n    });\r\n\r\n    // --- Parser CSV avanzado ---\r\n    function parseCSVAdvanced(text) {\r\n      let separador = \';\';\r\n      const rows = [];\r\n      let row = [];\r\n      let current = \'\';\r\n      let insideQuotes = false;\r\n\r\n      for (let i = 0; i < text.length; i++) {\r\n        const char = text[i];\r\n        const next = text[i + 1];\r\n\r\n        if (char === \'\"\' && insideQuotes && next === \'\"\') {\r\n          current += \'\"\'; // comilla escapada (\"\")\r\n          i++;\r\n        } else if (char === \'\"\') {\r\n          insideQuotes = !insideQuotes;\r\n        } else if (char === separador && !insideQuotes) {\r\n          row.push(current);\r\n          current = \'\';\r\n        } else if ((char === \'\\n\' || char === \'\\r\') && !insideQuotes) {\r\n          if (current || row.length) {\r\n            row.push(current);\r\n            rows.push(row);\r\n            row = [];\r\n            current = \'\';\r\n          }\r\n          if (char === \'\\r\' && next === \'\\n\') i++; // salto de línea Windows\r\n        } else {\r\n          current += char;\r\n        }\r\n      }\r\n\r\n      if (current || row.length) {\r\n        row.push(current);\r\n        rows.push(row);\r\n      }\r\n\r\n      return rows;\r\n    }\r\n\r\n    // --- Mostrar tabla ---\r\n    function renderTable(data) {\r\n      table.innerHTML = \'\';\r\n      if (!data.length) return;\r\n\r\n      const headers = data[0];\r\n      const thead = document.createElement(\'tr\');\r\n      headers.forEach(h => {\r\n        const th = document.createElement(\'th\');\r\n        th.textContent = h;\r\n        thead.appendChild(th);\r\n      });\r\n      table.appendChild(thead);\r\n\r\n      for (let i = 1; i < data.length; i++) {\r\n        const tr = document.createElement(\'tr\');\r\n        data[i].forEach(cell => {\r\n          const td = document.createElement(\'td\');\r\n          td.textContent = cell;\r\n          tr.appendChild(td);\r\n        });\r\n        table.appendChild(tr);\r\n      }\r\n    }\r\n\r\n    // --- Convertir a JSON ---\r\n    function renderJSON(data) {\r\n      if (data.length < 2) {\r\n        jsonOutput.textContent = \'[]\';\r\n        return;\r\n      }\r\n\r\n      const headers = data[0];\r\n      const json = data.slice(1).map(row => {\r\n        const obj = {};\r\n        headers.forEach((h, i) => {\r\n          obj[h] = row[i] ?? \'\';\r\n        });\r\n        return obj;\r\n      });\r\n\r\n      jsonOutput.textContent = JSON.stringify(json, null, 2);\r\n    }\r\n  </script>\r\n</body>\r\n</html>\r\n'),
