('3305FC93486A4D7F1EEE93585CFBAA49','2025-03-15 19:35:33','2025-09-26 11:33:38',0,'7D2C9C0CF3B893CA6EC55DC070198D44','productos_listado.js','//registrarse a eventos\r\n\r\nwindow.addEventListener(\"load\", onload);\r\nMyApp.utils.loadScript (\"modules/ventas/scripts/test.js\", onloadlib);\r\n\r\nfunction onload(){\r\n    MyApp.ui.notify(\'en el load del javascript\');\r\n    \r\n    const jsonQuery = `{\r\n        \"select\": [\r\n            \"*\"\r\n        ],\r\n        \"from\": {\r\n            \"table\": \"productos\",\r\n            \"alias\": \"\"\r\n        },\r\n        \"joins\": [],\r\n        \"where\": \"\",\r\n        \"groupBy\": [],\r\n        \"having\": \"\",\r\n        \"orderBy\": [],\r\n        \"limit\": null,\r\n        \"distinct\": true\r\n    }`;\r\n    \r\n    \r\n    MyApp.api.query((jsonQuery))\r\n    .then(data => { \r\n       let to_update = []; //en upsert se manda una lista de objetos a actualizar o insertar\r\n       \r\n        console.log(data); \r\n        data.forEach(producto => {\r\n            console.log(`Producto: ${producto.nombre}, Peso: ${producto.peso}`);\r\n            to_update.push({ id: producto.id, peso: 50});\r\n        });\r\n        //ahora agregamos tb uno nuevo\r\n        //to_update.push({nombre:\"uno nuevo desde JS\"});\r\n        \r\n        //llamamos a la api\r\n        MyApp.api.upsert(\"productos\",to_update).then( data => console.log(data) ).catch( error => console.log(error));\r\n        \r\n    } )\r\n    .catch( error => { \r\n        console.error(error);\r\n    });\r\n    \r\n}\r\nfunction onloadlib(){\r\n    MyApp.utils.log(\'Libreria cargada\');\r\n\r\n}\r\n\r\n','list'),
('70295DF2E962E20B5093655D0BF9CAAC','2025-09-23 06:55:26','2025-09-23 11:24:24',1,'20AB28F839675213390C76A55A267945','app_transiciones_proceso.js','document.addEventListener(\"DOMContentLoaded\", onload);\r\n\r\n\r\nfunction onload(){\r\n    //establecemos el prefiltro de los lookup\r\n    let id_proceso = MyApp.form.getFieldValue(\"id_proceso\" );\r\n    console.log(id_proceso);\r\n    \r\n    let obj = document.getElementById(\"id_fase_inicio\");\r\n    obj.setAttribute(\"data-field\",\"id_proceso\");\r\n    obj.setAttribute(\"data-value\",id_proceso);\r\n    \r\n    obj = document.getElementById(\"id_fase_fin\");\r\n    obj.setAttribute(\"data-field\",\"id_proceso\");\r\n    obj.setAttribute(\"data-value\",id_proceso);\r\n}\r\n\r\n','form'),
