('12B48A899E80857F736191FFD58754D3','test relacion ','select * from app_entity_columns where id_entity not in (select id from app_entities)',0,'{\n    \"select\": [\n        \"*\"\n    ],\n    \"from\": {\n        \"table\": \"app_entity_columns\",\n        \"alias\": \"\"\n    },\n    \"joins\": [],\n    \"where\": \"id_entity not in (select id from app_entities)\",\n    \"groupBy\": [],\n    \"having\": \"\",\n    \"orderBy\": [],\n    \"limit\": null,\n    \"distinct\": false\n}'),
('14890AF9A22EAC9F2DFD8C806F58968F','Update','update app_views set id_entity = (select id from app_entities where app_views.controller = app_entities.entity)',0,NULL),
('1CC6F9BBB87FADE5FB522E983A38274A','test','select e.* \r\n				from \r\n				app_roles_usuarios as ru \r\n				left join app_roles as r on ru.id_rol = r.id\r\n				left join app_permisos_entidad as pe on r.id = pe.id_rol\r\nleft join app_entities as e on pe.id_entity = e.id\r\n				where \r\n				ru.id_usuario = \'590A4A27433A5C3A85D4939810302137\'\r\nand e.id_module = \'2BAA56D57128A664AD18DF268E31E49E\'\r\nand pe.read_access = 1\r\n				group by pe.id_entity',0,'{\n    \"select\": [\n        \"e.*\"\n    ],\n    \"from\": {\n        \"table\": \"app_roles_usuarios\",\n        \"alias\": \"ru\"\n    },\n    \"joins\": [\n        {\n            \"type\": \"LEFT\",\n            \"table\": \"app_roles\",\n            \"alias\": \"r\",\n            \"on\": \"ru.id_rol = r.id\"\n        },\n        {\n            \"type\": \"LEFT\",\n            \"table\": \"app_permisos_entidad\",\n            \"alias\": \"pe\",\n            \"on\": \"r.id = pe.id_rol\"\n        },\n        {\n            \"type\": \"LEFT\",\n            \"table\": \"app_entities\",\n            \"alias\": \"e\",\n            \"on\": \"pe.id_entity = e.id\"\n        }\n    ],\n    \"where\": \"ru.id_usuario = \'590A4A27433A5C3A85D4939810302137\' and e.id_module = \'2BAA56D57128A664AD18DF268E31E49E\' and pe.read_access = 1\",\n    \"groupBy\": [\n        \"pe.id_entity\"\n    ],\n    \"having\": \"\",\n    \"orderBy\": [],\n    \"limit\": null,\n    \"distinct\": false\n}'),
('20C4539FA3B81FA443E0747CB2B8D5E4','permiso','select distinct ru.id, r.nombre, u.login\r\nfrom app_roles_usuarios as ru\r\nleft join usuarios as u on ru.id_usuario = u.id \r\nleft join app_roles as r on ru.id_rol = r.id \r\nlimit 1\r\n',0,'{\n    \"select\": [\n        \"ru.id\",\n        \"r.nombre\",\n        \"u.login\"\n    ],\n    \"from\": {\n        \"table\": \"app_roles_usuarios\",\n        \"alias\": \"ru\"\n    },\n    \"joins\": [\n        {\n            \"type\": \"LEFT\",\n            \"table\": \"usuarios\",\n            \"alias\": \"u\",\n            \"on\": \"ru.id_usuario = u.id\"\n        },\n        {\n            \"type\": \"LEFT\",\n            \"table\": \"app_roles\",\n            \"alias\": \"r\",\n            \"on\": \"ru.id_rol = r.id\"\n        }\n    ],\n    \"where\": \"\",\n    \"groupBy\": [],\n    \"having\": \"\",\n    \"orderBy\": [],\n    \"limit\": 1,\n    \"distinct\": true\n}'),
